import React, { Component } from "react";
import Navigation from "../components/Navigation";
import Footer from "../components/Footer";
import ListProductCategory from "../components/ProductCategoryList";
import { doSignOut } from "../store/actions/actionUser";
import { productCategory } from "../store/actions/actionProduct";
import { connect } from "react-redux";
import { allProducts } from "../store/actions/actionProduct";
// import { Redirect } from "react-router-dom";

class ProductCategory extends Component {
  componentDidMount() {
    this.props.allProducts();
    this.props.productCategory();
  }

  render() {
    console.log("cek cek cek", this.props);

    let productCategory = this.props.location.pathname;
    productCategory = productCategory.replace("/category/", "");
    productCategory = productCategory.replace(/-/gi, " ");
    // const productDetail = this.props.dataProduct.productList.filter(
    //   (item) => item.product_name === productName
    // );
    const productCategoryList = this.props.productList.productList;
    return (
      <div>
        <Navigation {...this.props} />
        <h1 className="text-center">Nama Kategori Produk</h1>
        <hr className="divider" />
        <div className="container">
          <div className="row list-product-home">
            {productCategoryList.map((value) => (
              <div className="col-md-3 pr-0">
                <ListProductCategory
                  {...this.props}
                  name={value.product_name}
                />
              </div>
            ))}
          </div>
        </div>

        <Footer />
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  dataUser: state.user,
  productList: state.product,
});

const mapDispatchToProps = {
  doSignOut,
  allProducts,
  productCategory,
};

export default connect(mapStateToProps, mapDispatchToProps)(ProductCategory);
