import axios from "axios";

export const getCart = () => {
    return (dispatch, getState) => {
        axios.get(process.env.REACT_APP_BASE_URL + "transaction", {
                headers: {
                    "Authorization": "Bearer " + getState().user.token
                }
            })
            .then((response) => {
                dispatch({
                    type: "GET_CUSTOMER_CART",
                    payload: response.data
                })
            })
            .catch((error) => {
                console.error(error)
            })
    }
}

export const postCart = (product_id) => {
    return (dispatch, getState) => {
        axios({
            method: "POST",
            url: process.env.REACT_APP_BASE_URL + "transaction",
            headers: { Authorization: `Bearer ${getState().user.token}` },
            params: {
                product_id: product_id,
                quantity: getState().cart.quantity
            }
        })
    }
}

export const updateInputQty = (event) => ({
    type: "UPDATE_INPUT_QUANTITY",
    payload: event
})
