import React, { Component } from "react";
import Navigation from "../components/Navigation";
import Footer from "../components/Footer";
import ListProduct from "../components/ListProduct";
import BestSeller from "../components/BestSeller";
// import { Redirect } from "react-router-dom";
// import { connect } from "react-redux";


class Home extends Component {
    render() {
        return (
            <div className="body">
                <Navigation {...this.props} />
                <div className="row mb-4">
                    <img className="img-banner" src={require("../assets/banner3.jpg")} style={{ width: "100%" }} alt="" />
                    {/* <img src={require("../assets/banner1.jpg")} style={{ width: "50%" }} alt="" /> */}
                </div>
                <div className="gif-home-1">
                    <img src={require("../assets/gif1.gif")} alt="" />
                </div>
                <div className="new-in-home">
                    {/* <img src={require("../assets/new-in.png")} alt="" /> */}
                </div>
                <h1 className="headline text-center mt-4">Best Sellers</h1>
                <BestSeller />
                <h1 className="headline text-center">New Arrivals</h1>
                <BestSeller />
                <h1 className="headline text-center">All Products</h1>
                <ListProduct />
                <hr />
                <Footer />
            </div>
        )
    }
}

export default Home;
