import React, { Component } from "react";
import Navigation from "../components/Navigation";
import Footer from "../components/Footer";
import ListProduct from "../components/ListProduct";
import { doSignOut } from "../store/actions/actionUser";
import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { allProducts } from "../store/actions/actionProduct";

class MyProducts extends Component {
  componentDidMount() {
    this.props.allProducts();
  }
  render() {
    const is_login = this.props.dataUser.is_login;
    const productList = this.props.productList.productList;

    return (
      <div>
        {/* {is_login ? ( */}
        <div>
          <Navigation {...this.props} />
          <div>
            <h1 className="text-center mt-3">My Products</h1>
            <hr className="divider" />
            <button className="add-products ml-5 mb-5">Add products</button>
          </div>
          {productList.map((value) => (
            <div>
              <ListProduct
                {...this.props}
                name={value.product_name}
                price={value.price}
                image={value.image}
              />
            </div>
          ))}
          <Footer />
        </div>
        {/* ) : (
                        <Redirect to={{ pathname: "/login" }}>
                        </Redirect>
                    )} */}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  dataUser: state.user,
  productList: state.product,
});

const mapDispatchToProps = {
  doSignOut,
  allProducts,
};

export default connect(mapStateToProps, mapDispatchToProps)(MyProducts);
