import React, { Component } from "react";
import Navigation from "../components/Navigation";
import Footer from "../components/Footer";
import ListProduct from "../components/ListProduct";
import BestSeller from "../components/BestSeller";
import { doSignOut } from "../store/actions/actionUser";
import { allProducts } from "../store/actions/actionProduct";
// import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

class Home extends Component {
  componentDidMount() {
    this.props.allProducts();
  }

  render() {
    console.log("cek props", this.props);
    return (
      <div className="body">
        <Navigation {...this.props} />
        <div className="row mb-4">
          <img
            className="img-banner"
            src={require("../assets/banner3.jpg")}
            style={{ width: "100%" }}
            alt=""
          />
          <div className="text-on-banner">
            <h1>THIS IS WELCOMING BANNER</h1>
          </div>
        </div>
        <h1 className="headline text-center">New Arrivals</h1>
        <hr className="divider my-2" />
        <BestSeller />
        <div>
          <h1 className="headline text-center mt-4">Best Sellers</h1>
          <hr className="divider my-2" />
          <BestSeller />
        </div>

        <div className="container p-0">
          <div className="row gif-home-1 mb-5 ml-5">
            <div className="col-md-6 pr-0">
              <img src={require("../assets/gif1.gif")} alt="" />
            </div>
            <div className="col-md-6 pl-0 text-center">
              <h1 className="mt-3 mx-5">EXPERIENCE OUR SEMERBAK</h1>
              <h3 className="premium-collection mb-4">Premium Collection</h3>
              <p className="mx-5">
                Breathe deeply.
                <br />
                Our essential oils and oil blends take you on a sensory journey
                that can instantly soothe and enliven both body and mind.
              </p>
              <Link to="/all-products">
                <button className="btn-shop-now">SHOP NOW</button>
              </Link>
            </div>
          </div>
          {/* <div className="row mini-banner-home">
                        <img src={require("../assets/mini-banner-home.jpg")} alt="" />
                    </div> */}
        </div>
        <hr className="mx-5" />
        <Footer />
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  dataUser: state.user,
});

const mapDispatchToProps = {
  doSignOut,
  allProducts,
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
