import React, { Component } from "react";
import Navigation from "../components/Navigation";
import Footer from "../components/Footer";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import {
  changeInputUsername,
  changeInputPassword,
  doLogin,
  doRegister,
} from "../store/actions/actionUser";

class Register extends Component {
  postRegister = async () => {
    await this.props.doRegister();
    const is_login = this.props.dataUser.is_login;
    if (is_login) {
      this.props.history.replace("/login");
    }
  };
  render() {
    return (
      <div>
        <Navigation {...this.props} />
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-sm-9 col-md-9 col-lg-5">
              <div className="card card-signin my-5">
                <div className="card-body">
                  <h1 className="card-title text-center">SIGN UP</h1>
                  <form
                    className="form-signin"
                    onSubmit={(e) => e.preventDefault()}
                  >
                    <div className="form-label-group">
                      <input
                        type="text"
                        id="inputEmail"
                        className="form-control"
                        placeholder="Username *"
                        required
                        onChange={(e) => this.props.changeInputUsername(e)}
                      />
                    </div>

                    <div className="form-label-group">
                      <input
                        type="password"
                        id="inputPassword"
                        className="form-control"
                        placeholder="Password *"
                        required
                        onChange={(e) => this.props.changeInputPassword(e)}
                      />
                    </div>
                    <p>
                      Already have an account?
                      <Link to="/login"> Sign in</Link>
                    </p>
                    <hr className="my-4" />
                    <button
                      className="btn btn-lg btn-primary btn-block text-uppercase"
                      type="submit"
                      onClick={() => this.postRegister()}
                    >
                      Continue
                    </button>
                  </form>
                </div>
              </div>
            </div>

            <div className="col-md-3">
              <div className="card card-signin my-5">
                <div className="card-body">
                  <p className="card-title">Need Assistance?</p>
                  <h3>Contact Our Customer Service Team</h3>
                  <p>custservice@title.com</p>
                  <p>
                    Our customer service team is available to take your emails
                    daily during the following business hours:
                  </p>
                  <p>Monday - Friday : 09.00 - 17.00</p>
                </div>
              </div>
            </div>
          </div>
        </div>
        <Footer />
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  dataUser: state.user,
});

const mapDispatchToProps = {
  changeInputUsername,
  changeInputPassword,
  doLogin,
  doRegister,
};

export default connect(mapStateToProps, mapDispatchToProps)(Register);
