import React, { Component } from "react";
import Navigation from "../components/Navigation";
import Footer from "../components/Footer";
import ListProduct from "../components/ListProduct";
import BestSeller from "../components/BestSeller";
// import { Redirect } from "react-router-dom";
// import { connect } from "react-redux";


class Home extends Component {
    render() {
        return (
            <div className="body">
                <Navigation {...this.props} />
                <div className="row mb-4">
                    <img className="img-banner" src={require("../assets/banner3.jpg")} style={{ width: "100%" }} alt="" />
                    <div className="text-on-banner">
                        <h1>THIS IS WELCOMING BANNER</h1>
                    </div>
                </div>
                <h1 className="headline text-center">New Arrivals</h1>
                <hr class="divider my-2" />
                <BestSeller />
                <div className="container">
                    <div className="row gif-home-1">
                        <img src={require("../assets/gif1.gif")} alt="" />
                    </div>
                    <div className="row mini-banner-home">
                        <img src={require("../assets/mini-banner-home.jpg")} alt="" />
                    </div>
                </div>
                <div className="row">
                    <h1 className="headline text-center mt-4">Best Sellers</h1>
                    <hr class="divider my-2" />
                    <BestSeller />
                </div>
                <div>
                    <h1 className="headline text-center">All Products</h1>
                    <hr class="divider my-2" />
                    <ListProduct />
                </div>
                <hr />
                <Footer />
            </div>
        )
    }
}

export default Home;
