import React, { Component } from "react";
import Navigation from "../components/Navigation";
import Footer from "../components/Footer";
import ProductInCart from "../components/ProductInCart";
import { doSignOut } from "../store/actions/actionUser";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { Redirect } from "react-router-dom";
import { productCategory } from "../store/actions/actionProduct";
import { getCart } from "../store/actions/actionCart";

class ShoppingCart extends Component {
  componentDidMount() {
    this.props.getCart();
    this.props.productCategory();
  }

  render() {
    const is_login = this.props.dataUser.is_login;
    const cart = this.props.dataCart;

    return (
      <div>
        {is_login ? (
          <div>
            <Navigation {...this.props} />
            <div className="container mx-5">
              <table class="table table-hover">
                <thead>
                  <tr>
                    <th scope="col">No.</th>
                    <th scope="col">Item Descriptions</th>
                    <th scope="col">Quantity</th>
                    <th scope="col">Price</th>
                    <th scope="col">Total</th>
                  </tr>
                </thead>
                <tbody>
                  <ProductInCart />
                </tbody>
              </table>

              <Link to="/checkout">
                <button className="btn-checkout">CHECK OUT</button>
              </Link>
            </div>
            <Footer />
          </div>
        ) : (
          <Redirect to={{ pathname: "/login" }}></Redirect>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  dataUser: state.user,
  dataCatagory: state.product,
  dataCart: state.cart,
});

const mapDispatchToProps = {
  doSignOut,
  productCategory,
  getCart,
};

export default connect(mapStateToProps, mapDispatchToProps)(ShoppingCart);
